import { useState } from 'react';
import { Header } from "../../components/Header";
import background from "../../assets/background.png";
import ItemList from "../../components/ItemList";

import './styles.css';

function App() {
  const [user, setUser] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const [repos, setRepos] = useState(null);

  const handleGetData = async () => {
    const userData = await fetch(`https://api.github.com/users/${user}`);
    const newUser = await userData.json();

    if (newUser.name) {
      const { avatar_url, name, bio } = newUser;
      setCurrentUser({ avatar_url, name, bio });

      const reposData = await fetch(`https://api.github.com/users/${user}/repos`);
      const newRepos = await reposData.json();

      if (newRepos.length) {
        setRepos(newRepos);
      }
    }

  }

  return (
    <div className="App">
      <Header />
      <div className="context">
        <img src={background} className="background" alt="background app" />
        <div className="info">
          <div>
            <input
              nome="User"
              value={user}
              onChange={event => setUser(event.target.value)}
              placeholder="@username" />
            <button onClick={handleGetData}>Search</button>
          </div>
          <div className="profile">
            <img
              // TODO mudar a foto de perfil dinamicamente
              src="https://avatars.githubusercontent.com/u/115806014?v=4"
              className="image_profile"
              alt="Profile"
            />
            <div>
              {/* TODO Colocar o nome e a descrição dinamicamente através da API do GitHub */}
              <h3>Italo Thiago</h3>
              <span>@italothiagodev</span>
              <p>Description</p>
            </div>
          </div>
          <hr />
          <div>
            <h4 className="repositorio">Repositórios</h4>
            <ItemList title="Teste 1" description="teste de descrição"/ >
            <ItemList title="Teste 2" description="teste de descrição"/ >
            <ItemList title="Teste 3" description="teste de descrição"/ >
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
